import React, { Component } from "react";
import './App.css';
import { Switch, Route, BrowserRouter, Redirect } from "react-router-dom";
import AuthenticationGuard from "./Authentication/AuthenticationGuard";
import Login from "./Authentication/Login";
import Register from "./Authentication/Register";
import Home from "./Home/Home";
import Dashboard from "./Dashboard/Dashboard";
import { database, auth, googleAuthProvider, storage } from './firebase';


function PrivateRoute ({component: Component, currentUser, ...rest}) {
  return (
    <Route
      {...rest}
      render={(props) => currentUser
        ? <Component {...props} />
        : <Redirect to={{pathname: '/login', state: {from: props.location}}} />}
    />
  )
}

function PublicRoute ({component: Component, currentUser, ...rest}) {
  return (
    <Route
      {...rest}
      render={(props) => currentUser
        ? <Component {...props} />
        : <Redirect to='/dashboard' />}
    />
  )
}
export default class App extends Component {
  constructor() {
    super()
    this.state = {
      currentUser: {}
    }
  }

  componentDidMount() {
    auth().onAuthStateChanged((user) => {
      if (user) {
        this.setState({
          currentUser: user,
        });
      } else {
        this.setState({
          authenticated: false,
        });
      }
    });
  }

  render() {
    return (
      <BrowserRouter>
        <Switch>
          <Route exact path={"/"} component={Home} />
          <PublicRoute currentUser={this.state.currentUser} path={"/login"} component={Login} />
          <PublicRoute currentUser={this.state.currentUser} path={"/Register"} component={Register} />
          <PrivateRoute currentUser={this.state.currentUser} path='/dashboard' component={Dashboard} />
        </Switch>
      </BrowserRouter>
    );
  }
}
